import { createSlice } from '@reduxjs/toolkit';

const initialState = {
	categoreId: 0,
	sort: {
		name: 'популярности',
		sortProperty: 'rating',
	},
};

const filterSlice = createSlice({
	name: 'filters',
	initialState,
	reducers: {
		setCategoryId(state) {
			state.categoreId;
		},
	},
});
